import { Component } from '@angular/core';
import { FormGroup , FormControl } from '@angular/forms';
import { AccountService } from '../../Services/account.service';





@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {

  constructor(private accountSer : AccountService){}

  loginForm = new FormGroup({
    userName: new FormControl(''),
    password: new FormControl(''),
  });



  public onSubmit (data : FormGroup) {
    const username = data.controls['userName'].value;
    const password = data.controls['password'].value;
    this.accountSer.login(username , password);
    console.log('loginOk' , data);
  }
}

<div class="container h-100 d-flex justify-content-center align-items-center">
  <form [formGroup]="loginForm" (ngSubmit)="onSubmit(this.loginForm)">
    <div class="row">
      <div class="col-12 mb-2">
        <label for="input-userName" class="form-label">نام کاربری</label>
        <input type="text" class="form-control line-height-btn-xsm" id="input-userName " aria-describedby="emailHelp" formControlName="userName">
      </div>
      <div class="col-12 mb-2">
        <label for="input-password" class="form-label">رمز عبور</label>
        <input type="password" class="form-control line-height-btn-xsm" id="input-password" formControlName="password">
      </div>
    </div>
    <div class="w-100 d-flex justify-content-center">
      <button type="submit" class="btn btn-primary w-50 mt-3 line-height-btn-xsm">ورود</button>
    </div>
  </form>
</div>
