import { Inject, Injectable } from '@angular/core';

import { HttpClient, HttpHeaders, HttpErrorResponse } from '@angular/common/http';
import { Observable, pipe } from 'rxjs';
import { branch } from '../Models/BranchModel';

const httpOptions = {
  headers: new HttpHeaders({ 'Content-Type': 'application/json; charset=utf-8' })
};

@Injectable({
  providedIn: 'root'
})


export class BranchService {
  protected apiUrl : string;
  
  constructor(private http : HttpClient, @Inject('BASE_URL') _baseUrl: string) {
    this.apiUrl = _baseUrl;
  }



  public getAllBranch () : Observable<any> {
    const url = `${this.apiUrl}Branch/getallbranches`;
    return this.http.get(url,httpOptions);
  }

  public createBranch (data : branch) {
    const userJson = JSON.stringify(data);
    const url = `${this.apiUrl}/${'register'}`;
    return this.http.post<Brand>(url,userJson,httpOptions);
  }
}
